import React, { useEffect, useState, useRef } from "react";
import Navbar from "./components/Navbar";
import "./styles/index.css";
import henryImage from "./assets/henry.png";
import sswmsimage from "./assets/sswms.png";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
function App() {


    // Enhanced intersection observer for stats
    const statsSection = document.querySelector("#stats");
    if (statsSection) {
      const observer = new IntersectionObserver(
        (entries) => {
          if (entries[0].isIntersecting) {
            animateCounters();
            observer.disconnect();
          }
        },
        { threshold: 0.5 }
      );
      observer.observe(statsSection);
    }
gsap.registerPlugin(ScrollTrigger);

  const introRef = useRef(null);
  const circleRef = useRef(null);
  const imgRef = useRef(null);
  const textRef = useRef(null);

  useEffect(() => {
    // Greeting animation
    gsap.fromTo(
      introRef.current.querySelectorAll("span"),
      { opacity: 0, x: -50 },
      {
        opacity: 1,
        x: 0,
        stagger: 0.2,
        duration: 1,
        scrollTrigger: {
          trigger: introRef.current,
          start: "top 80%",
        },
      }
    );

    // Circle scale animation
    gsap.fromTo(
      circleRef.current,
      { scale: 0.5, opacity: 0 },
      {
        scale: 2,
        opacity: 1,
        duration: 2,
        ease: "power3.out",
        scrollTrigger: {
          trigger: circleRef.current,
          start: "top 70%",
          scrub: true, // smooth while scrolling
          pin: true,   // stays pinned while animating
        },
      }
    );

    // Image fade-up
    gsap.fromTo(
      imgRef.current,
      { y: 100, opacity: 0 },
      {
        y: 0,
        opacity: 1,
        duration: 1.5,
        scrollTrigger: {
          trigger: imgRef.current,
          start: "top 80%",
        },
      }
    );

    // Text fade-up
    gsap.fromTo(
      textRef.current,
      { y: 100, opacity: 0 },
      {
        y: 0,
        opacity: 1,
        duration: 1.5,
        scrollTrigger: {
          trigger: textRef.current,
          start: "top 80%",
        },
      }
    );
  }, []);
  return (
    <>
      <Navbar />
      <section className="hero text-white" id="home">
        <div className="container">
          <div className="row align-items-center">
            <div
              className="col-lg-6"
              data-aos="fade-right"
              data-aos-duration="1000"
            >
              <img
                src={henryImage}
                alt="Profile"
                className="rounded-circle mb-4 border border-5 border-warning animate__animated animate__zoomIn animate__delay-1s"
                style={{
                  width: "200px",
                  height: "300px",
                  objectFit: "cover",
                }}
              />

              <h1 className="display-4 fw-bold mb-3">
                Hi, I'm <span className="text-warning">Henryco Buena</span>
              </h1>
              <h2 className="h3 mb-4">
                I'm a <span className="text-warning">{typedText}</span>
                <span className="typing-cursor">|</span>
              </h2>
              <p className="lead mb-4">
                Passionate about creating digital experiences that make a
                difference. I build modern, responsive, and user-friendly
                applications.
              </p>
              <div className="d-flex gap-3 flex-wrap">
                <a
                  href="#projects"
                  className="btn btn-warning btn-lg px-4 py-2 rounded-pill animate__animated animate__pulse animate__infinite animate__slow"
                >
                  View My Work
                </a>
                <a
                  href="#contact"
                  className="btn btn-outline-light btn-lg px-4 py-2 rounded-pill"
                >
                  Get In Touch
                </a>
              </div>
            </div>
            <div
              className="col-lg-6 text-center"
              data-aos="fade-left"
              data-aos-duration="1000"
              data-aos-delay="200"
            >
              <div className="position-relative">
                <img
                  src="https://images.unsplash.com/photo-1555066931-4365d14bab8c?w=500&h=400&fit=crop"
                  alt="Coding"
                  className="img-fluid rounded-3 shadow-lg animate__animated animate__fadeInUp animate__delay-2s"
                  style={{ maxWidth: "500px" }}
                />
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Stats Section */}

      <section className="about" id="aboutSection">
        <div className="container--main">
          <div className="container--inner">
            <div className="about__animations">
              {/* Intro */}
              <div className="about__intro" ref={introRef}>
                <div className="greeting--container">
                  <h1 className="greeting">
                    <span>Hi, I'm</span>{" "}
                    <span className="name__1st">Henryco</span>{" "}
                    <span className="name__2nd">D. Buena</span>
                  </h1>
                </div>
                <div className="circle__container">
                  <div className="circle" ref={circleRef}></div>
                </div>
              </div>

              {/* Main Content */}
              <div className="about__main">
                <div className="about__img--container">
                  <img
                    className="about__img"
                    src="group-photo.4f83f472.jpeg"
                    alt="group"
                    ref={imgRef}
                  />
                </div>
                <div className="about__text--container" ref={textRef}>
                  <p className="work-experience">
                    I am Henryco D. Buena, a passionate web developer...
                  </p>
                  <div className="about-me">
                    <h2>Key Achievements</h2>
                    <ul>
                      <li>
                        <strong>RESTful API:</strong> Designed scalable{" "}
                        <span className="highlight">RESTful APIs</span>.
                      </li>
                      <li>
                        <strong>Mentorship:</strong>{" "}
                        <span className="highlight">Code reviews</span> &
                        mentoring.
                      </li>
                      <li>
                        <strong>Full Stack:</strong> React.js, Node.js,
                        PostgreSQL
                      </li>
                    </ul>

                    <h2>Skills & Specializations</h2>
                    <p>
                      Responsive websites using{" "}
                      <span className="highlight">mobile-first workflow</span>.
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
      {/* Bootstrap Icons CDN */}
      <link
        href="https://cdnjs.cloudflare.com/ajax/libs/bootstrap-icons/1.10.0/font/bootstrap-icons.min.css"
        rel="stylesheet"
      />

      {/* Bootstrap JS */}
      <script src="https://cdnjs.cloudflare.com/ajax/libs/bootstrap/5.3.0/js/bootstrap.bundle.min.js"></script>

      {/* AOS JS */}
      <script src="https://cdnjs.cloudflare.com/ajax/libs/aos/2.3.4/aos.js"></script>
    </>
  );
}

export default App;
