import React, { useEffect, useState } from "react";
import Navbar from "./components/Navbar";
import "./styles/index.css";
import henryImage from "./assets/henry.png";
import sswmsimage from "./assets/sswms.png";
import { gsap } from "gsap";
 useEffect(() => {
   const ctx = gsap.context(() => {
     // ✅ Intro text animation (Hi, I'm Antonio Carlo Autor)
     gsap.from(".greeting span", {
       x: -100,
       opacity: 0,
       stagger: 0.3,
       scrollTrigger: {
         trigger: ".about__intro",
         start: "top center",
         end: "bottom center",
         scrub: true,
         pin: true, // stays in place during scroll
       },
     });

     // ✅ Circle background animation
     gsap.from(".circle", {
       y: -200,
       opacity: 0,
       scrollTrigger: {
         trigger: ".about__intro",
         start: "top center",
         scrub: true,
       },
     });

     // ✅ Main content (image + text)
     gsap.from(".about__img, .about__text--container", {
       y: 100,
       opacity: 0,
       stagger: 0.3,
       scrollTrigger: {
         trigger: ".about__main",
         start: "top center",
         end: "bottom center",
         scrub: true,
         pin: true, // also pinned separately
       },
     });
   }, sectionRef);

   return () => ctx.revert();
 }, []);
function App() {
  const [typedText, setTypedText] = useState("");
