import React from "react";
import { projects } from "../data/projects";

function ProjectsGrid() {
  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
      {projects.map((project) => (
        <div key={project.id} className="border p-4 rounded-lg shadow">
          <img src={project.image} alt={project.title} className="w-full h-40 object-cover rounded" />
          <h2 className="text-lg font-bold mt-2">{project.title}</h2>
          <p className="text-sm">{project.description}</p>
          <p className="text-xs mt-1 text-gray-500">{project.technologies.join(", ")}</p>
        </div>
      ))}
    </div>
  );
}

export default ProjectsGrid;
