import React, { useEffect, useState, useRef } from "react";
import Navbar from "./components/Navbar";
import "./styles/index.css";
import henryImage from "./assets/henry.png";
import sswmsimage from "./assets/sswms.png";

function App() {
  const [typedText, setTypedText] = useState("");
  const [currentIndex, setCurrentIndex] = useState(0);
  const [activeFilter, setActiveFilter] = useState("All");
  const [selectedProject, setSelectedProject] = useState(null);
  const [selectedExperience, setSelectedExperience] = useState(null);

  const sectionRef = useRef(null); // ✅ define ref here

  // Roles for typing animation
  const roles = [
    "Frontend Developer",
    "Full Stack Developer",
    "Freelancer",
    "Problem Solver",
  ];

  // ✅ Typing animation effect
  useEffect(() => {
    const currentRole = roles[currentIndex];
    let charIndex = 0;

    const typeInterval = setInterval(() => {
      if (charIndex <= currentRole.length) {
        setTypedText(currentRole.slice(0, charIndex));
        charIndex++;
      } else {
        clearInterval(typeInterval);
        setTimeout(() => {
          setCurrentIndex((prev) => (prev + 1) % roles.length);
          setTypedText("");
        }, 2000);
      }
    }, 80);

    return () => clearInterval(typeInterval);
  }, [currentIndex]);

  // ✅ Intersection observer for fade-in animations
  useEffect(() => {
    const observerOptions = { threshold: 0.1 };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        const element = entry.target;
        if (entry.isIntersecting) {
          element.classList.add("fade-in");
          element.classList.remove("fade-out");
        } else {
          element.classList.remove("fade-in");
          element.classList.add("fade-out");
        }
      });
    }, observerOptions);

    const fadeElements = document.querySelectorAll(".fade-scroll");
    fadeElements.forEach((el) => observer.observe(el));

    return () => observer.disconnect();
  }, []);

  // ✅ Stats counter animation
  useEffect(() => {
    const counters = document.querySelectorAll(".counter");

    const animateCounters = () => {
      counters.forEach((counter) => {
        const target = parseInt(counter.getAttribute("data-target"));
        const count = parseInt(counter.innerText);
        const increment = target / 100;

        if (count < target) {
          counter.innerText = Math.ceil(count + increment);
          setTimeout(animateCounters, 20);
        } else {
          counter.innerText = target;
        }
      });
    };

    const statsSection = document.querySelector("#stats");
    if (statsSection) {
      const observer = new IntersectionObserver(
        (entries) => {
          if (entries[0].isIntersecting) {
            animateCounters();
            observer.disconnect();
          }
        },
        { threshold: 0.5 }
      );
      observer.observe(statsSection);
    }
  }, []);

  return (
    <>
      <Navbar />

      {/* HERO SECTION */}
      <section className="hero text-white" id="home">
        <div className="container">
          <div className="row align-items-center">
            {/* Left Column */}
            <div
              className="col-lg-6"
              data-aos="fade-right"
              data-aos-duration="1000"
            >
              <img
                src={henryImage}
                alt="Profile"
                className="rounded-circle mb-4 border border-5 border-warning animate__animated animate__zoomIn animate__delay-1s"
                style={{ width: "200px", height: "300px", objectFit: "cover" }}
              />
              <h1 className="display-4 fw-bold mb-3">
                Hi, I'm <span className="text-warning">Henryco Buena</span>
              </h1>
              <h2 className="h3 mb-4">
                I'm a <span className="text-warning">{typedText}</span>
                <span className="typing-cursor">|</span>
              </h2>
              <p className="lead mb-4">
                Passionate about creating digital experiences that make a
                difference. I build modern, responsive, and user-friendly
                applications.
              </p>
              <div className="d-flex gap-3 flex-wrap">
                <a
                  href="#projects"
                  className="btn btn-warning btn-lg px-4 py-2 rounded-pill animate__animated animate__pulse animate__infinite animate__slow"
                >
                  View My Work
                </a>
                <a
                  href="#contact"
                  className="btn btn-outline-light btn-lg px-4 py-2 rounded-pill"
                >
                  Get In Touch
                </a>
              </div>
            </div>

            {/* Right Column */}
            <div
              className="col-lg-6 text-center"
              data-aos="fade-left"
              data-aos-duration="1000"
              data-aos-delay="200"
            >
              <div className="position-relative">
                <img
                  src="https://images.unsplash.com/photo-1555066931-4365d14bab8c?w=500&h=400&fit=crop"
                  alt="Coding"
                  className="img-fluid rounded-3 shadow-lg animate__animated animate__fadeInUp animate__delay-2s"
                  style={{ maxWidth: "500px" }}
                />
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Example Stats Section */}
      <section
        id="stats"
        className="py-5 text-center bg-light"
        ref={sectionRef}
      >
        <h2 className="mb-4">My Stats</h2>
        <div className="row">
          <div className="col-md-4">
            <h3 className="counter" data-target="120">
              0
            </h3>
            <p>Projects Completed</p>
          </div>
          <div className="col-md-4">
            <h3 className="counter" data-target="5">
              0
            </h3>
            <p>Years Experience</p>
          </div>
          <div className="col-md-4">
            <h3 className="counter" data-target="30">
              0
            </h3>
            <p>Happy Clients</p>
          </div>
        </div>
      </section>
    </>
  );
}

export default App;
