import React, { useState, useEffect, useRef } from "react";
import Navbar from "./components/Navbar";
import "./styles/index.css";
import henryImage from "./assets/henry.png";
import sswmsimage from "./assets/sswms.png";

// âœ… GSAP for animations
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
gsap.registerPlugin(ScrollTrigger);

function App() {
  const [typedText, setTypedText] = useState("");
  const [currentIndex, setCurrentIndex] = useState(0);

  const roles = [
    "Frontend Developer",
    "Full Stack Developer",
    "Freelancer",
    "Problem Solver",
  ];

  // Typing animation
  useEffect(() => {
    const currentRole = roles[currentIndex];
    let charIndex = 0;

    const typeInterval = setInterval(() => {
      if (charIndex <= currentRole.length) {
        setTypedText(currentRole.slice(0, charIndex));
        charIndex++;
      } else {
        clearInterval(typeInterval);
        setTimeout(() => {
          setCurrentIndex((prev) => (prev + 1) % roles.length);
          setTypedText("");
        }, 2000);
      }
    }, 80);

    return () => clearInterval(typeInterval);
  }, [currentIndex]);

  return (
    <>
      <Navbar />
      {/* âœ… HERO */}
      <section
        className="hero text-white bg-dark min-vh-100 d-flex align-items-center"
        id="home"
      >
        <div className="container">
          <div className="row align-items-center">
            {/* Left Side */}
            <div className="col-lg-6">
              <img
                src={henryImage}
                alt="Profile"
                className="rounded-circle mb-4 border border-5 border-warning"
                style={{ width: "200px", height: "300px", objectFit: "cover" }}
              />
              <h1 className="display-4 fw-bold mb-3">
                Hi, I'm <span className="text-warning">Henryco Buena</span>
              </h1>
              <h2 className="h3 mb-4">
                I'm a <span className="text-warning">{typedText}</span>
                <span className="typing-cursor">|</span>
              </h2>
              <p className="lead mb-4">
                Passionate about creating digital experiences that make a
                difference. I build modern, responsive, and user-friendly
                applications.
              </p>
              <div className="d-flex gap-3 flex-wrap">
                <a
                  href="#projects"
                  className="btn btn-warning btn-lg px-4 py-2 rounded-pill"
                >
                  View My Work
                </a>
                <a
                  href="#contact"
                  className="btn btn-outline-light btn-lg px-4 py-2 rounded-pill"
                >
                  Get In Touch
                </a>
              </div>
            </div>

            {/* Right Side */}
            <div className="col-lg-6 text-center">
              <img
                src="https://images.unsplash.com/photo-1555066931-4365d14bab8c?w=500&h=400&fit=crop"
                alt="Coding"
                className="img-fluid rounded-3 shadow-lg"
                style={{ maxWidth: "500px" }}
              />
            </div>
          </div>
        </div>
      </section>

      {/* âœ… ABOUT SECTION */}
      <AboutSection />

      {/* âœ… Bootstrap Icons */}
      <link
        href="https://cdnjs.cloudflare.com/ajax/libs/bootstrap-icons/1.10.0/font/bootstrap-icons.min.css"
        rel="stylesheet"
      />
    </>
  );
}

/* ===========================
   ABOUT SECTION COMPONENT
   =========================== */
function AboutSection() {
  const sectionRef = useRef(null);

  useEffect(() => {
    const ctx = gsap.context(() => {
      // Greeting text animation
      gsap.from(".greeting span", {
        x: -100,
        opacity: 0,
        stagger: 0.3,
        scrollTrigger: {
          trigger: ".about__intro",
          start: "top center",
          end: "bottom center",
          scrub: true,
          pin: true,
        },
      });

      // Circle animation
      gsap.from(".circle", {
        y: -200,
        opacity: 0,
        scrollTrigger: {
          trigger: ".about__intro",
          start: "top center",
          scrub: true,
        },
      });

      // Image + text
      gsap.from(".about__img, .about__text--container", {
        y: 100,
        opacity: 0,
        stagger: 0.3,
        scrollTrigger: {
          trigger: ".about__main",
          start: "top center",
          end: "bottom center",
          scrub: true,
          pin: true,
        },
      });
    }, sectionRef);

    return () => ctx.revert();
  }, []);

  return (
    <section
      ref={sectionRef}
      id="aboutSection"
      className="about bg-light min-vh-100 py-5 d-flex align-items-center"
    >
      <div className="container">
        {/* Intro */}
        <div className="about__intro text-center position-relative mb-5">
          <h1 className="greeting display-4 fw-bold">
            <span>Hi, I'm</span>{" "}
            <span className="name__1st text-primary">Antonio</span>{" "}
            <span className="name__2nd text-warning">Carlo Autor</span>
          </h1>
          <div className="circle__container position-absolute top-50 start-50 translate-middle">
            <div
              className="circle rounded-circle bg-gradient"
              style={{ width: "200px", height: "200px" }}
            ></div>
          </div>
        </div>

        {/* Main */}
        <div className="about__main row align-items-center">
          <div className="col-md-6">
            <img
              src={sswmsimage}
              alt="group photo"
              className="about__img img-fluid rounded shadow"
            />
          </div>
          <div className="col-md-6 about__text--container">
            <p>
              I am a 28-year-old web developer with 2 years of experience in
              front-end development and 1 year as a full-stack developer. During
              my career, I've led a cross-functional team to develop an
              innovative attendance monitoring app.
            </p>
            <h3>Key Achievements</h3>
            <ul>
              <li>
                <strong>RESTful API Design:</strong> Designed scalable APIs for
                integration.
              </li>
              <li>
                <strong>Mentorship:</strong> Conducted code reviews and guided
                juniors.
              </li>
              <li>
                <strong>Full Stack:</strong> React.js, Node.js, PostgreSQL
                expertise.
              </li>
            </ul>
            <h3>Skills & Specializations</h3>
            <p>
              I specialize in responsive, mobile-first websites that are both
              visually appealing and functional.
            </p>
          </div>
        </div>
      </div>
    </section>
  );
}

export default App;
