sourcesContent:
import React, { useEffect, useState, useRef } from "react";
import Navbar from "./components/Navbar";
import "./styles/index.css";
import henryImage from "./assets/henry.png";
import sswmsimage from "./assets/sswms.png";

function App() {
  const [typedText, setTypedText] = useState("");
  const [currentIndex, setCurrentIndex] = useState(0);
  const [activeFilter, setActiveFilter] = useState("All");
  const [selectedProject, setSelectedProject] = useState(null);
  const [selectedExperience, setSelectedExperience] = useState(null);

  const roles = [
    "Frontend Developer",
    "Full Stack Developer",
    "Freelancer",
    "Problem Solver",
  ];

  const projects = [
    {
      title: "E-commerce Platform",
      description:
        "Full-stack online shopping platform with admin dashboard, payment integration, and inventory management.",
      fullDescription:
        "A comprehensive e-commerce solution built with modern technologies. Features include user authentication, product catalog with search and filtering, shopping cart functionality, secure payment processing via Stripe, order management, admin dashboard with analytics, inventory tracking, and responsive design for all devices.",
      image: sswmsimage,
      technologies: ["React", "Node.js", "MongoDB", "Stripe"],
      category: "Full Stack",
      link: "#",
      github: "#",
      features: [
        "User authentication and authorization",
        "Product catalog with search and filters",
        "Shopping cart and wishlist functionality",
        "Secure payment processing with Stripe",
        "Order tracking and management",
        "Admin dashboard with analytics",
        "Inventory management system",
        "Responsive design for all devices",
      ],
      challenges:
        "Implementing real-time inventory updates and handling concurrent user sessions during checkout process.",
      duration: "3 months",
      teamSize: "4 developers",
    },
    {
      title: "Food Delivery App",
      description:
        "React + PHP-based Grab clone with real-time tracking, payment processing, and restaurant management.",
      fullDescription:
        "A complete food delivery application similar to Grab, featuring real-time order tracking, integrated payment systems, restaurant management dashboard, and customer support features. Built with React frontend and PHP backend with MySQL database.",
      image:
        "https://images.unsplash.com/photo-1565299624946-b28f40a0ca4b?w=400&h=300&fit=crop",
      technologies: ["React", "PHP", "MySQL", "Firebase"],
      category: "Mobile",
      link: "#",
      github: "#",
      features: [
        "Real-time order tracking with GPS",
        "Restaurant management dashboard",
        "Multi-payment gateway integration",
        "Push notifications for order updates",
        "Rating and review system",
        "Delivery partner app",
        "Customer support chat",
        "Promotional codes and discounts",
      ],
      challenges:
        "Implementing real-time GPS tracking and managing multiple user roles (customers, restaurants, delivery partners).",
      duration: "4 months",
      teamSize: "6 developers",
    },
    {
      title: "Portfolio Website",
      description:
        "Responsive React portfolio with modern animations, contact forms, and project showcases.",
      fullDescription:
        "A modern, responsive portfolio website showcasing professional work and skills. Features smooth animations, interactive elements, contact form integration, and optimized performance. Built with React and styled with Bootstrap for a professional appearance.",
      image:
        "https://images.unsplash.com/photo-1467232004584-a241de8bcf5d?w=400&h=300&fit=crop",
      technologies: ["React", "Bootstrap", "CSS3", "JavaScript"],
      category: "Frontend",
      link: "#",
      github: "#",
      features: [
        "Responsive design for all devices",
        "Smooth scroll animations",
        "Interactive project galleries",
        "Contact form with validation",
        "Skills visualization charts",
        "Testimonials carousel",
        "Blog integration ready",
        "SEO optimized structure",
      ],
      challenges:
        "Optimizing animations for performance while maintaining visual appeal across different devices and browsers.",
      duration: "1 month",
      teamSize: "1 developer (solo project)",
    },
    {
      title: "Task Management System",
      description:
        "Comprehensive project management tool with team collaboration, time tracking, and reporting features.",
      fullDescription:
        "A full-featured project management application designed for team collaboration. Includes task creation and assignment, time tracking, project timelines, team communication tools, and comprehensive reporting dashboard with data visualization.",
      image:
        "https://images.unsplash.com/photo-1611224923853-80b023f02d71?w=400&h=300&fit=crop",
      technologies: ["React", "Firebase", "Material-UI", "Chart.js"],
      category: "Full Stack",
      link: "#",
      github: "#",
      features: [
        "Kanban board for task management",
        "Time tracking and reporting",
        "Team collaboration tools",
        "Project timeline visualization",
        "File sharing and comments",
        "Custom project templates",
        "Advanced filtering and search",
        "Export reports to PDF/Excel",
      ],
      challenges:
        "Designing an intuitive interface that scales from small teams to large organizations while maintaining performance.",
      duration: "5 months",
      teamSize: "3 developers",
    },
    {
      title: "Weather Analytics Dashboard",
      description:
        "Advanced weather tracking with data visualization, forecasting, and location-based alerts.",
      fullDescription:
        "An advanced weather analytics platform that provides detailed weather information, forecasting, and data visualization. Features interactive charts, location-based weather alerts, historical data analysis, and integration with multiple weather APIs for accurate predictions.",
      image:
        "https://images.unsplash.com/photo-1504608524841-42fe6f032b4b?w=400&h=300&fit=crop",
      technologies: ["React", "D3.js", "Weather API", "Bootstrap"],
      category: "Frontend",
      link: "#",
      github: "#",
      features: [
        "Real-time weather data visualization",
        "7-day weather forecasting",
        "Interactive weather maps",
        "Location-based weather alerts",
        "Historical weather data analysis",
        "Multiple weather metrics display",
        "Customizable dashboard widgets",
        "Export weather reports",
      ],
      challenges:
        "Processing and visualizing large amounts of weather data while maintaining smooth user interactions and real-time updates.",
      duration: "2 months",
      teamSize: "2 developers",
    },
    {
      title: "Learning Management System",
      description:
        "Educational platform with course management, progress tracking, and interactive quizzes.",
      fullDescription:
        "A comprehensive learning management system designed for educational institutions. Features course creation and management, student progress tracking, interactive quizzes and assignments, video conferencing integration, and detailed analytics for instructors.",
      image:
        "https://images.unsplash.com/photo-1522202176988-66273c2fd55f?w=400&h=300&fit=crop",
      technologies: ["React", "Laravel", "MySQL", "WebRTC"],
      category: "Full Stack",
      link: "#",
      github: "#",
      features: [
        "Course creation and management",
        "Interactive quiz and assignment system",
        "Student progress tracking",
        "Video conferencing integration",
        "Discussion forums and chat",
        "Grade book and analytics",
        "Certificate generation",
        "Mobile-responsive interface",
      ],
      challenges:
        "Implementing real-time video conferencing and ensuring scalable architecture for thousands of concurrent users during online classes.",
      duration: "6 months",
      teamSize: "5 developers",
    },
  ];

  const experiences = [
    {
      title: "Senior Frontend Developer",
      company: "Tech Solutions Inc.",
      period: "2023 - Present",
      description:
        "Leading frontend development team, implementing modern React applications with focus on performance and user experience.",
      achievements: [
        "Improved app performance by 40%",
        "Led team of 5 developers",
        "Implemented CI/CD pipeline",
      ],
    },
    {
      title: "Full Stack Developer",
      company: "Digital Agency Co.",
      period: "2021 - 2023",
      description:
        "Developed end-to-end web applications using React, Node.js, and various database technologies.",
      achievements: [
        "Built 15+ client projects",
        "Reduced development time by 30%",
        "Mentored junior developers",
      ],
    },
    {
      title: "Junior Developer",
      company: "StartUp Hub",
      period: "2020 - 2021",
      description:
        "Started career building responsive websites and learning modern development practices.",
      achievements: [
        "Completed 20+ projects",
        "Learned 5+ technologies",
        "Achieved 98% client satisfaction",
      ],
    },
  ];

  const testimonials = [
    {
      name: "Sarah Johnson",
      role: "Project Manager at TechCorp",
      content:
        "Outstanding developer with excellent problem-solving skills. Delivered our project ahead of schedule with exceptional quality.",
      avatar:
        "https://images.unsplash.com/photo-1494790108755-2616b612b786?w=100&h=100&fit=crop&crop=face",
    },
    {
      name: "Michael Chen",
      role: "CEO at StartupXYZ",
      content:
        "Transformed our vision into reality. The attention to detail and technical expertise exceeded our expectations.",
      avatar:
        "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=100&h=100&fit=crop&crop=face",
    },
    {
      name: "Emily Rodriguez",
      role: "Design Director",
      content:
        "Perfect collaboration between design and development. The final product was pixel-perfect and performant.",
      avatar:
        "https://images.unsplash.com/photo-1438761681033-6461ffad8d80?w=100&h=100&fit=crop&crop=face",
    },
  ];

  // Tech Stack Data
  const techCategories = [
    {
      title: "PROGRAMMING & SCRIPTING LANGUAGES",
      items: [
        "HTML",
        "CSS",
        "JavaScript",
        "TypeScript",
        "PHP",
        "Python",
        "Java",
        "R",
        "Go",
        "Rust",
      ],
    },
    {
      title: "FRAMEWORKS & LIBRARIES",
      items: [
        "Laravel",
        "React",
        "Django",
        "Flask",
        "Next.js",
        "Tailwind",
        "Shadcn UI",
        "Vue.js",
        "Angular",
      ],
    },
    {
      title: "DATABASES & TOOLS",
      items: [
        "MySQL",
        "PostgreSQL",
        "MongoDB",
        "Redis",
        "Supabase",
        "Firebase",
        "Prisma",
        "NoSQL",
      ],
    },
    {
      title: "AI & MACHINE LEARNING",
      items: [
        "TensorFlow",
        "PyTorch",
        "Keras",
        "OpenCV",
        "LangChain",
        "Hugging Face",
        "OpenAI",
        "Claude",
      ],
    },
    {
      title: "DEVOPS & TOOLS",
      items: [
        "Docker",
        "Kubernetes",
        "Git",
        "GitHub",
        "Vercel",
        "AWS",
        "VS Code",
        "Linux",
        "CI/CD",
      ],
    },
    {
      title: "DESIGN & PROTOTYPING",
      items: [
        "Figma",
        "Adobe XD",
        "Photoshop",
        "Illustrator",
        "Canva",
        "Framer",
        "Sketch",
        "InVision",
      ],
    },
  ];

  const programmingLanguages = [
    { name: "Python", color: "#3776ab" },
    { name: "JavaScript", color: "#f7df1e" },
    { name: "React", color: "#61dafb" },
    { name: "PHP", color: "#777bb4" },
    { name: "Java", color: "#ed8b00" },
    { name: "C#", color: "#239120" },
    { name: "Django", color: "#092e20" },
    { name: "Flutter", color: "#02569b" },
    { name: "Go", color: "#00add8" },
    { name: "Rust", color: "#000000" },
    { name: "TypeScript", color: "#3178c6" },
    { name: "Laravel", color: "#ff2d20" },
    { name: "Vue.js", color: "#4fc08d" },
    { name: "Angular", color: "#dd0031" },
    { name: "Node.js", color: "#339933" },
  ];

  // Filter projects based on active filter
  const filteredProjects =
    activeFilter === "All"
      ? projects
      : projects.filter((p) => p.category === activeFilter);

  // Handle opening project modal
  const handleOpenProjectModal = (project) => {
    setSelectedProject(project);
    const modal = new window.bootstrap.Modal(
      document.getElementById("projectModal")
    );
    modal.show();
  };

  // Handle opening experience modal
  const handleOpenExperienceModal = (exp) => {
    setSelectedExperience(exp);
    const modal = new window.bootstrap.Modal(
      document.getElementById("experienceModal")
    );
    modal.show();
  };

  useEffect(() => {
    const observerOptions = {
      threshold: 0.1,
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        const element = entry.target;
        if (entry.isIntersecting) {
          element.classList.add("fade-in");
          element.classList.remove("fade-out");
        } else {
          element.classList.remove("fade-in");
          element.classList.add("fade-out");
        }
      });
    }, observerOptions);

    const fadeElements = document.querySelectorAll(".fade-scroll");
    fadeElements.forEach((el) => observer.observe(el));

    return () => observer.disconnect();
  }, []);

  // Enhanced typing animation with better timing
  useEffect(() => {
    const currentRole = roles[currentIndex];
    let charIndex = 0;

    const typeInterval = setInterval(() => {
      if (charIndex <= currentRole.length) {
        setTypedText(currentRole.slice(0, charIndex));
        charIndex++;
      } else {
        clearInterval(typeInterval);
        setTimeout(() => {
          setCurrentIndex((prev) => (prev + 1) % roles.length);
          setTypedText("");
        }, 2000);
      }
    }, 80);

    return () => clearInterval(typeInterval);
  }, [currentIndex]);

  // Initialize AOS and other animations
  useEffect(() => {
    // Initialize AOS when it's available
    const initAOS = () => {
      if (window.AOS) {
        window.AOS.init({
          duration: 800,
          easing: "ease-in-out",
          once: true,
          offset: 100,
          delay: 0,
        });
      }
    };

    // Wait for AOS to load
    if (window.AOS) {
      initAOS();
    } else {
      const checkAOS = setInterval(() => {
        if (window.AOS) {
          initAOS();
          clearInterval(checkAOS);
        }
      }, 100);
    }

    // Enhanced scroll effects and navbar
    const counters = document.querySelectorAll(".counter");

    const animateCounters = () => {
      counters.forEach((counter) => {
        const target = parseInt(counter.getAttribute("data-target"));
        const count = parseInt(counter.innerText);
        const increment = target / 100;

        if (count < target) {
          counter.innerText = Math.ceil(count + increment);
          setTimeout(animateCounters, 20);
        } else {
          counter.innerText = target;
        }
      });
    };

    const nav = document.querySelector(".navbar");
    if (nav) {
      const onScroll = () => {
        if (window.scrollY > 50) {
          nav.classList.add("scrolled");
        } else {
          nav.classList.remove("scrolled");
        }
      };
      window.addEventListener("scroll", onScroll);
    }

    // Enhanced intersection observer for stats
    const statsSection = document.querySelector("#stats");
    if (statsSection) {
      const observer = new IntersectionObserver(
        (entries) => {
          if (entries[0].isIntersecting) {
            animateCounters();
            observer.disconnect();
          }
        },
        { threshold: 0.5 }
      );
      observer.observe(statsSection);
    }
  }, []);
  const introRef = useRef(null);
  const circleRef = useRef(null);
  const imgRef = useRef(null);
  const textRef = useRef(null);
  const mainRef = useRef(null);

  useEffect(() => {
    // Simple scroll-based animations without GSAP
    const handleScroll = () => {
      const scrollY = window.scrollY;
      const windowHeight = window.innerHeight;

      if (introRef.current && circleRef.current) {
        const introRect = introRef.current.getBoundingClientRect();
        const scrollProgress = Math.max(0, Math.min(1, (windowHeight - introRect.top) / windowHeight));

        // Animate greeting text
        const greetingSpans = introRef.current.querySelectorAll('.greeting span');
        greetingSpans.forEach((span, index) => {
          const delay = index * 0.2;
          const progress = Math.max(0, Math.min(1, (scrollProgress - delay) / 0.3));
          span.style.opacity = progress;
          span.style.transform = `translateX(${(1 - progress) * -50}px)`;
        });

        // Animate circle
        const circleProgress = Math.max(0, Math.min(1, scrollProgress));
        const scale = 0.5 + (circleProgress * 1.5);
        circleRef.current.style.transform = `scale(${scale})`;
        circleRef.current.style.opacity = circleProgress;
      }

      // Animate main content
      if (mainRef.current) {
        const mainRect = mainRef.current.getBoundingClientRect();
        const mainProgress = Math.max(0, Math.min(1, (windowHeight - mainRect.top) / windowHeight));

        if (imgRef.current) {
          imgRef.current.style.opacity = mainProgress;
          imgRef.current.style.transform = `translateY(${(1 - mainProgress) * 100}px)`;
        }

        if (textRef.current) {
          textRef.current.style.opacity = mainProgress;
          textRef.current.style.transform = `translateY(${(1 - mainProgress) * 100}px)`;
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Initial call

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

